{
    "size": 5000,
    "query": {
        "bool": {
            "must": [
                {
                    "term": {
                        "isSent": {
                            "value": 1,
                            "boost": 1.0
                        }
                    }
                },
                {
                    "term": {
                        "event_status": {
                            "value": 1,
                            "boost": 1.0
                        }
                    }
                },
                {
                    "terms": {
                        "monitor_obj": [
                            11098
                        ],
                        "boost": 1.0
                    }
                },
                {
                    "range": {
                        "alert_time": {
                            "from": 1604937600000,
                            "to": 1605023999000,
                            "include_lower": true,
                            "include_upper": true,
                            "boost": 1.0
                        }
                    }
                }
            ],
            "adjust_pure_negative": true,
            "boost": 1.0
        }
    },
    "aggregations": {
        "time": {
            "date_histogram": {
                "field": "alert_time",
                "interval": "1d",
                "offset": -28800000,
                "format" : "yyyy-MM-dd",
                "order": {
                    "_key": "asc"
                },
                "keyed": false,
                "min_doc_count": 0
            },
            "aggregations": {
                "unMarked": {
                    "sum": {
                        "script": {
                            "source": "if(doc['isMark'].value=='1' && doc['markPeople'].value==''){1} else {0}",
                            "lang": "painless"
                        }
                    }
                },
                "marked": {
                    "sum": {
                        "script": {
                            "source": "if(doc['isMark'].value=='1' && doc['markPeople'].value!=''){1} else {0}",
                            "lang": "painless"
                        }
                    }
                },
                "real": {
                    "sum": {
                        "script": {
                            "source": "if(doc['isMark'].value=='1' && doc['markPeople'].value!='' && doc['isAlert'].value=='2'){1} else {0}",
                            "lang": "painless"
                        }
                    }
                }
            }
        }
    }
}

